from scrapy.http.response.html import HtmlResponse
from scrapy.selector import HtmlXPathSelector, XmlXPathSelector

def body2xxs(ctx, encoding='utf-8', url='http://localhost'):
    r = HtmlResponse(url,body=ctx,encoding=encoding)
    return XmlXPathSelector(r)

def body2hxs(ctx, encoding='utf-8', url='http://localhost'):
    r = HtmlResponse(url,body=ctx,encoding=encoding)
    return HtmlXPathSelector(r)

def extract(xxs, rule, join_flag=True ):
    if xxs is None:     return ''
    rs = xxs.select(rule)
    if rs is None : return ''
        
    txt = rs.extract()
    if txt is None:     return ''
        
    rs = [e.encode('utf8','ignore').strip()  for e in txt]
    if join_flag:
        return ''.join(rs)
        
    return rs 



